      { label: "Menu" },
      { label: "HeaderBar" },
      { label: "Table" },
      { label: "List" },
    ],
  },
];

export default () => {
  const [selected, setSelected] = React.useState("Notebook");

  return (
    <List border={false} fill sidebar="navigation" size="medium">
      {sections.map((s) => (
        <>
          <ListItem key={s.name} title>
            {s.name}
          </ListItem>
          {s.items.map((item) => (
            <ListItem
